export const giscus = (options) => {
    if (!options.config.giscus)
        return [];
    return [
        {
            type: 'element',
            tagName: 'script',
            properties: {
                ...options.config.giscus,
            },
            children: [],
        },
        {
            type: 'element',
            tagName: 'div',
            properties: {
                class: 'giscus',
            },
            children: [],
        },
    ];
};
